import React, { useState } from 'react';
import axios from 'axios';

const ExpenseSystem = () => {
  const [category, setCategory] = useState('飛行機');
  const [file, setFile] = useState(null);
  const [feedback, setFeedback] = useState('');

  const handleFileChange = (e) => {
    setFile(e.target.files[0]);
  };

  const handleSubmit = async () => {
    if (!file) {
      alert('ファイルを選択してください。');
      return;
    }

    const formData = new FormData();
    formData.append('file', file);
    formData.append('category', category);

    try {
      const response = await axios.post('https://expense-backend-1-ubp4.onrender.com/api/upload', formData, {  
        headers: {
          'Content-Type': 'multipart/form-data',
        },
      });
      setFeedback(response.data.feedback);
    } catch (error) {
      console.error('エラーが発生しました。', error);
      setFeedback('エラーが発生しました。もう一度お試しください。');
    }
  };

  return (
    <div className="p-8">
      <h1 className="text-xl mb-4">経費精算システム</h1>

      <div className="mb-4">
        <label>カテゴリを選択:</label>
        <select value={category} onChange={(e) => setCategory(e.target.value)} className="ml-2 p-2 border rounded">
          <option value="飛行機">飛行機</option>
          <option value="ホテル">ホテル</option>
          <option value="バス">バス</option>
        </select>
      </div>

      <div className="mb-4">
        <input type="file" accept=".png, .jpg, .jpeg, .pdf" onChange={handleFileChange} />
      </div>

      <button onClick={handleSubmit} className="p-2 bg-blue-500 text-white rounded">ファイルをアップロード</button>

      {feedback && (
        <div className="mt-4 p-4 border rounded bg-gray-100">
          <pre>{feedback}</pre>
        </div>
      )}
    </div>
  );
};

export default ExpenseSystem;
